import * as echarts from 'echarts/dist/echarts.js';
import $ from 'jquery'


$(document).ready(function () {

    var arraySensorDataData = [];
    var arraySensorDataValues = [];
    var mediaSensorData = [];
    var aux = 0;
 
    aux = 0
      for(var i=0 ; i<10 ; i++){
   
    // $("divSensorDataData").each(function () {
        arraySensorDataData[aux] = '10/10/10'
        // $(this).attr('arData');
        aux++;
    }
    aux = 0;
    for(var i=0 ; i<10 ; i++){
    // $('divSensorDataValue').each(function () {
        arraySensorDataValues[aux] = 10
        // $(this).attr('arValue');
        mediaSensorData[aux] = 10
        // $(this).attr('media');
        aux++;
    // });
    }
   

    var divChartSensorData = document.getElementById('mainSensorData');
    if (divChartSensorData != null) {
        console.log(echarts)
        var myChart3 = echarts.init(divChartSensorData);
        var app3 = {
            count: 0
        };
        echarts.option = {
            title: {
                show: true,
                textStyle: {
                },
                text: "Média das medidas",
                left: '20px'
               
            },
       


            xAxis: {
                name: 'Data',
                type: 'category',
                data: arraySensorDataData,
            },
            yAxis: {
                name: 'Valor',
                type: 'value'
            },
            dataZoom: {
                
                show: true,
                id: 'dataZoomX',
                type: 'slider',
                filterMode: 'filter',
               
            },
            series: [{
                name: 'Valor',
                data: arraySensorDataValues,
                type: 'bar'
            },
                {
                    name: 'Média',
                    type: 'line',
                    data: mediaSensorData,
                }],
            tooltip: {
                trigger: 'axis',
                
                axisPointer: {

                    type: 'cross',
                    label: {
                        backgroundColor: '#283b56'
                    }
                }
            }, legend: {
                left: 'center',
                data: ['Valor', 'Média'] //Remover o "DESATIVADO" para ativar
            },
            toolbox: {
                left: '950px',

                show: true,
                feature: {
                    saveAsImage: { title: "Baixar" }

                }
            }, grid: {
                bottom: 80
            },
            
        };

        app3.count = 11;
        if (arraySensorDataValues != null && arraySensorDataValues.length > 0) {
            // document.getElementById('noData3').removeAttribute("style");
            myChart3.setOption(echarts.option);
            console.log("TAAQ")
        } else {
                console.log("ta nao")

            $("#mainSensorData").empty();
            // document.getElementById('mainSensorData').removeAttribute("style");
            $("#noData3").text("Sem dados disponiveis");
        }



    }
})
