import React,{Component} from 'react'
import {Dropdown,InputGroup,DropdownButton,FormControl,Button} from 'react-bootstrap'
import './switch.css'
import './chart.js'
import './caixa.css'
import ReactEcharts from 'echarts-for-react';
 
// render echarts option.

class Caixa extends Component{
    
    constructor(props){
        super(props)
    
        this.state = {
          isLoading: false
        }
    }
    
    getOtion(){
        var arraySensorDataData = [];
        var arraySensorDataValues = [];
        var mediaSensorData = [];
        var aux = 0;
     
        aux = 0
          for(var i=0 ; i<10 ; i++){
       
        // $("divSensorDataData").each(function () {
            arraySensorDataData[aux] = '10/10/10'
            // $(this).attr('arData');
            aux++;
        }
        aux = 0;
        for(var i=0 ; i<10 ; i++){
        // $('divSensorDataValue').each(function () {
            arraySensorDataValues[aux] = 10
            // $(this).attr('arValue');
            mediaSensorData[aux] = 10
            // $(this).attr('media');
            aux++;
        // });
        }
       

        return(

                {
                title: {
                    show: true,
                    textStyle: {
                    },
                    text: "Média das medidas",
                    left: '20px'
                
                },
        


                xAxis: {
                    name: 'Data',
                    type: 'category',
                    data: arraySensorDataData,
                },
                yAxis: {
                    name: 'Valor',
                    type: 'value'
                },
                dataZoom: {
                    
                    show: true,
                    id: 'dataZoomX',
                    type: 'slider',
                    filterMode: 'filter',
                
                },
                series: [{
                    name: 'Valor',
                    data: arraySensorDataValues,
                    type: 'bar'
                },
                    {
                        name: 'Média',
                        type: 'line',
                        data: mediaSensorData,
                    }],
                tooltip: {
                    trigger: 'axis',
                    
                    axisPointer: {

                        type: 'cross',
                        label: {
                            backgroundColor: '#283b56'
                        }
                    }
                }, legend: {
                    left: 'center',
                    data: ['Valor', 'Média'] //Remover o "DESATIVADO" para ativar
                },
                toolbox: {
                    left: '950px',

                    show: true,
                    feature: {
                        saveAsImage: { title: "Baixar" }

                    }
                }, grid: {
                    bottom: 80
                },
                
            }
        );
    }


    render(){
        return(

            <div>
                <br/>
                    <h2>Caixa</h2>
                <br/>
           
                <div className="Content  forf col-12">
                    <div className="col-6">
                        <h4>Relatório</h4>
                    </div>
                    <div className="col-6">
                        <h4>Gráfico</h4>
                        <div className="container col-lg12 col-md-12 col-sm-12">
                        {/* style=" width:550px; height:50px; float:left; padding-bottom:100px; " */}
                            <h3 id="noData3"></h3>
                            {/* style="width: 1100px; height: 500px; float: left;" */}
                            <div id="mainSensorData" ></div>
                            <ReactEcharts 
                                option={this.getOtion()} 
                                style={{height: '400px', width: '100%'}}  
                                opts={{renderer: 'svg'}}  
                                className='react_for_echarts' />
                        </div>
                    </div>
                </div>


            </div>


        );
    }



}






export {Caixa}
