        startDate = new Date(selected.date.valueOf());
        startDate.setDate(startDate.getDate(new Date(selected.date.valueOf())));
        $('.to').datepicker('setStartDate', startDate);
    }); 
    
    $('.to').datepicker({
        autoclose: true,
        minViewMode: 1,
